                function validateVNPhone(phone) {
                    const countryCodeRegx = /^(\+84|84)+/;
                    const phoneValidRegx = /^[0-9]+$/;
                    const phoneRegx = /^((032|033|034|035|036|037|038|039|086|096|097|098|081|082|083|084|085|088|091|094|056|058|092|070|076|077|078|079|089|090|093|099|059)[0-9]{7})$/;
                    const errorCodes = {
                        INVALID_LENGTH: {
                            valid: false,
                            message: 'Số điện thoại phải là 10 số'
                        },
                        INVALID_CHARACTERS: {
                            valid: false,
                            message: 'Số điện thoại không hợp lệ'
                        },
                        VALID_PHONE: {
                            valid: true,
                            message: 'Số điện thoại hợp lệ'
                        },
                    }

                    phone = phone.toString();
                    phone = phone.replace(countryCodeRegx, 0)

                    if(!phoneValidRegx.test(phone)) {
                        return errorCodes.INVALID_CHARACTERS;
                    }
                    if(phone.length < 10 || phone.length > 10) {
                        return errorCodes.INVALID_LENGTH;
                    }
                    if(!phoneRegx.test(phone)) {
                        return errorCodes.INVALID_CHARACTERS;
                    }
                    return errorCodes.VALID_PHONE;
                }

