                function horizontalScrollContainer() {
                    const container_width = $('.banner-slide .container').width();
                    const screen_width = $(window).width();
                    if (screen_width <= 767) {
                        bannerSlider();
                        return;
                    }
                    if ($('.banner-slide__wrapper').hasClass('slick-slider')) {
                        $('.banner-slide__wrapper').slick("unslick");
                    }
                    $('.horizontal-scroll-wrapper').css('padding-top', (screen_width - container_width) / 2);
                    return (screen_width - container_width) / 2;
                }

                function horizontalNextScroll(scroll_top, slide_scroll_position, banner_item, banner_slide) {
                    const banner_item_width = (banner_item.outerWidth() + 30);
                    scroll_top = scroll_top + banner_item_width;
                    if (Math.floor(scroll_top) >= Math.floor(slide_scroll_position + banner_item_width)) scroll_top = 0;
                    if (Math.floor(scroll_top) > Math.floor(slide_scroll_position)) scroll_top = slide_scroll_position;
                    if (!$('.banner-slide .horizontal-scroll-wrapper').hasClass('is-hover')) {
                        banner_slide.animate({
                            scrollTop: scroll_top
                        })
                    }
                    setTimeout(function (e) {
                        horizontalAutoScroll();
                    }, 3000)
                }

                function horizontalAutoScroll() {
                    if (!$('.banner-slide__arrow--prev').hasClass('slick-arrow')) {
                        const banner_slide = $('.banner-slide .horizontal-scroll-wrapper');
                        let container_width = horizontalScrollContainer();
                        if (banner_slide.length <= 0) return;
                        let banner_item = banner_slide.children();
                        let width_width = $(window).width(),
                            slide_view_width = width_width - container_width,
                            slide_container_width = (banner_item.outerWidth() + 30) * banner_item.length,
                            slide_scroll_position = slide_container_width - slide_view_width;
                        let scroll_top = banner_slide.scrollTop();
                        horizontalNextScroll(scroll_top, slide_scroll_position, banner_item, banner_slide)
                    }
                }

                function horizontalScroll() {
                    const banner_slide = $('.banner-slide .horizontal-scroll-wrapper');
                    if (banner_slide.length <= 0) return;

                    let container_width = horizontalScrollContainer();

                    let banner_item = banner_slide.children(),
                        scroll_top = 0;

                    let width_width = $(window).width(),
                        slide_view_width = width_width - container_width,
                        slide_container_width = (banner_item.outerWidth() + 30) * banner_item.length,
                        slide_scroll_position = slide_container_width - slide_view_width;

                    $(window).resize(function () {
                        container_width = horizontalScrollContainer();

                        width_width = $(window).width();
                        slide_view_width = width_width - container_width;
                        slide_container_width = (banner_item.outerWidth() + 30) * banner_item.length;
                        slide_scroll_position = slide_container_width - slide_view_width;
                    });

                    banner_slide.scroll(function (e) {
                        scroll_top = $(this).scrollTop();
                    })

                    let check_next = true,
                        check_prev = true

                    $(document).on('click', '.banner-slide__arrow--prev', function (e) {
                        if (!$(this).hasClass('slick-arrow') && check_next) {
                            e.preventDefault();
                            const banner_item_width = (banner_item.outerWidth() + 30)
                            scroll_top = scroll_top + banner_item_width;
                            if (Math.floor(scroll_top) >= Math.floor(slide_scroll_position + banner_item_width)) scroll_top = 0;
                            if (Math.floor(scroll_top) > Math.floor(slide_scroll_position)) scroll_top = slide_scroll_position;
                            banner_slide.animate({
                                scrollTop: scroll_top
                            })
                            check_next = false;
                            setTimeout(function () {
                                check_next = true;
                            }, 300)
                        }
                    })

                    $(document).on('click', '.banner-slide__arrow--next', function (e) {
                        if (!$(this).hasClass('slick-arrow') && check_prev) {
                            e.preventDefault();
                            const banner_item_width = (banner_item.outerWidth() + 30)
                            scroll_top = scroll_top - banner_item_width;
                            if (Math.floor(scroll_top) <= Math.floor(0 - banner_item_width)) scroll_top = slide_scroll_position;
                            if (Math.floor(scroll_top) < 0) scroll_top = 0;
                            banner_slide.animate({
                                scrollTop: scroll_top
                            })
                            check_prev = false;
                            setTimeout(function () {
                                check_prev = true;
                            }, 300)
                        }
                    });

                    setTimeout(function (e) {
                        horizontalAutoScroll();
                    }, 3000)

                    $(document).on('mousemove', '.banner-slide .horizontal-scroll-wrapper', function () {
                        $(this).addClass('is-hover');
                    });

                    $(document).on('mouseleave', '.banner-slide .horizontal-scroll-wrapper', function () {
                        $(this).removeClass('is-hover');
